import React, { useState } from 'react'; 
import './App.css';

function App() {
  let [ counter, changeCounter] = useState(1);
  window.changeCounter = changeCounter;

  return (
    <div>
      <h1>{counter}</h1>
      <h4>{counter}</h4>
      <buttonon onClick={() => changeCounter(counter++)}>Click</buttonon>
    </div>

  );
}

export default App;
